    private void setViewMode() {
        // Set text field enable/disable based on the mode
        viewMode = !viewMode;
        txtLastName.setEditable(viewMode);
        txtFirstName.setEditable(viewMode);
        txtDOB.setEditable(viewMode);


        if(viewMode == false)
        {
            txtLastName.setBackground(Color.LIGHT_GRAY);
            txtFirstName.setBackground(Color.LIGHT_GRAY);
            txtDOB.setBackground(Color.LIGHT_GRAY);

        }
        else
        {
            txtLastName.setBackground(Color.WHITE);
            txtFirstName.setBackground(Color.WHITE);
            txtDOB.setBackground(Color.WHITE);
        }
    }
    
    private void btnAddPatientActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddPatientActionPerformed
        
        try
        {
            Connection con = ehr340.DBUtils.MakeConnection();
            String storedProc = "CALL insertPatient (?, ?, ?)";
            PreparedStatement statement = con.prepareStatement(storedProc);
            statement.setString(1, txtLastName.getText());
            statement.setString(2, txtFirstName.getText());
            statement.setString(3, txtDOB.getText());
            statement.executeUpdate();
            con.close();
        }
        catch(Exception e){}
        loadTable();
    }//GEN-LAST:event_btnAddPatientActionPerformed

